class AreaConverter():
    def squareyard_squarefeet_or_foot(sy):
        """
        here f
        1 square yard = 9 suare feet
        
        """
        res=9*sy
        return res
    def acre_squarefeet_or_foot(acr):
        """
        here f
        1 acre = 43560 suare feet
        
        """
        res=acr*43560
        return res

